import React, { Component } from 'react';

export default class PrivateServer extends Component {
    constructor(props) {
        super(props);
        this.state = {
        };

        this.logout = this.logout.bind(this);
    }

    componentDidMount () {
        const chatSocket = new WebSocket(
            'ws://127.0.0.1:8000/ws/p'
            + this.props.name
            + '/'
        );

        chatSocket.onmessage = function(e) {
            const data = JSON.parse(e.data);
            console.log(e);
            document.querySelector('#chat-log').value += (data.message + '\n');
        };

        chatSocket.onclose = function(e) {
            console.error('Chat socket closed unexpectedly');
        };

        document.querySelector('#chat-message-input').focus();
        document.querySelector('#chat-message-input').onkeyup = function(e) {
            if (e.keyCode === 13) {
                document.querySelector('#chat-message-submit').click();
            }
        };

        document.querySelector('#chat-message-submit').onclick = function(e) {
            const messageInputDom = document.querySelector('#chat-message-input');
            const message = messageInputDom.value;
            chatSocket.send(JSON.stringify({
                'message': message
            }));
            messageInputDom.value = '';
        };
    }

    logout () {
        var nameString = 'AllowedTo_' + this.props.name
        localStorage.removeItem(nameString);
        this.props.history.push("/join")
    }

    render() {
        return (
            <div className="server">
                <textarea id="chat-log" cols="100" rows="20"/><br/>
                <input id="chat-message-input" type="text" size="100"/>
                <input id="chat-message-submit" type="button" value="Send"/><br/>

                <input type="button" onClick={this.logout} value="Logout"/>
            </div>
        );
    }
}
