import React, { Component } from 'react';
import './css/Join.css';

export default class Join extends Component {
    constructor(props) {
        super(props);
        this.state = {
            message: '',
            server_type: '',
            password: '',
        }

        this.handleNameChange = this.handleNameChange.bind(this);
        this.handlePasswordChange = this.handlePasswordChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleType = this.handleType.bind(this);
    }

    handleNameChange (e) {
        this.setState({
            name: e.target.value
        });
    }

    handlePasswordChange (e) {
        this.setState({
            password: e.target.value
        });
    }

    handleType (e) {
        if (e.target.id === "s") {
            document.querySelector(".password").style.display = "none";
            e.target.style.display = "none";
            document.querySelector(".privateChange").style.display = "block";
        }
        if (e.target.id === "p") {
            document.querySelector(".password").style.display = "block";
            e.target.style.display = "none";
            document.querySelector(".publicChange").style.display = "block";
        }

        this.setState({
            server_type: e.target.value
        });
    }

    handleSubmit () {
        const requestOptions = {
            method: 'POST',
            headers: {'Content-Type' : 'application/json'},
            body: JSON.stringify({
                name: this.state.name,
                password: this.state.password
            }),
        };
        fetch('http://127.0.0.1:8000/api/' + this.state.server_type + '/join/', requestOptions)
            .then((response) => {
                if (response.ok) {
                    var setString = 'AllowedTo_' + this.state.name
                    localStorage.setItem(setString, true);
                    this.props.history.push("/" + this.state.server_type + "/" + this.state.name)
                }
                else {
                    this.setState({message: response.statusText})
                }
            })
        }

    render() {
        return(
            <div className="Join">
                {this.state.message}

                <input className="name" type="text" onChange={this.handleNameChange} />
                <input className="submit" onClick={this.handleSubmit} type="button" value="â†’" />

                <input className="password" type="text" onChange={this.handlePasswordChange} />             


                <div onClick={this.handleType} id="p" className="privateChange">Join Private Server?</div>
                <div onClick={this.handleType} id="s" className="publicChange">Join Public Server?</div>
            </div>
        );
    }
}
