import React, { Component } from 'react';
import { TextField, Button, Grid, Typography, Link, ButtonGroup } from "@material-ui/core";

export default class Join extends Component {
    constructor(props) {
        super(props);
        this.state = {
            message: '',
            server_type: '',
            password: '',
        }

        this.handleNameChange = this.handleNameChange.bind(this);
        this.handlePasswordChange = this.handlePasswordChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleType = this.handleType.bind(this);
    }

    handleNameChange (e) {
        this.setState({
            name: e.target.value
        });
    }

    handlePasswordChange (e) {
        this.setState({
            password: e.target.value
        });
    }

    handleType (e) {
        this.setState({
            server_type: e.target.value
        });
    }

    handleSubmit () {
        const requestOptions = {
            method: 'POST',
            headers: {'Content-Type' : 'application/json'},
            body: JSON.stringify({
                name: this.state.name,
                password: this.state.password
            }),
        };
        fetch('http://127.0.0.1:8000/api/' + this.state.server_type + '/join/', requestOptions)
            .then((response) => {
                if (response.ok) {
                    var setString = 'AllowedTo_' + this.state.name
                    localStorage.setItem(setString, true);
                    this.props.history.push("/" + this.state.server_type + "/" + this.state.name)
                }
                else {
                    this.setState({message: response.statusText})
                }
            })
        }

    render() {
        return(
            <div className="Join">
            <Grid container spacing={1}>
            <Grid item xs={12} align="center">
            <Typography variant="h4" component="h4">
                Join a Room
            </Typography>
            </Grid>
            <Grid item xs={12} align="center">
            <TextField
                error={this.state.error}
                label="Code"
                placeholder="Enter a Room Code"
                value={this.state.roomCode}
                helperText={this.state.error}
                variant="outlined"
                onChange={this.handleTextFieldChange}
            />
            </Grid>
            <Grid item xs={12} align="center">
            <Button
                variant="contained"
                color="primary"
                onClick={this.roomButtonPressed}
            >
                Enter Room
            </Button>
            </Grid>
            <Grid item xs={12} align="center">
            <Button variant="contained" color="secondary" to="/" component={Link}>
                Back
            </Button>
            </Grid>
            </Grid>


            <Grid container spacing={3}>
        <Grid item xs={12} align="center">
          <Typography variant="h3" compact="h3">
            House Party
          </Typography>
        </Grid>
        <Grid item xs={12} align="center">
          <ButtonGroup disableElevation variant="contained" color="primary">
            <Button color="primary" to="/join" component={Link}>
              Join a Room
            </Button>
            <Button color="default" to="/info" component={Link}>
              Info
            </Button>
            <Button color="secondary" to="/create" component={Link}>
              Create a Room
            </Button>
          </ButtonGroup>
        </Grid>
      </Grid>

      <Grid container spacing={1}>
        <Grid item xs={12} align="center">
          <Typography variant="h4" component="h4">
            Code: {this.roomCode}
          </Typography>
        </Grid>
        {this.state.isHost ? this.renderSettingsButton() : null}
        <Grid item xs={12} align="center">
          <Button
            variant="contained"
            color="secondary"
            onClick={this.leaveButtonPressed}
          >
            Leave Room
          </Button>
        </Grid>
      </Grid>

                {this.state.message}
                Name:
                <input type="text" onChange={this.handleNameChange}/><br/>
                Password:
                <input type="text" onChange={this.handlePasswordChange}/><br/><br/>               

                Server Type:<br/>
                <input type="radio" id="public-server" name="server-type" value="s" onChange={this.handleType}/>
                <label htmlFor="public-server">Public</label><br/>
                <input type="radio" id="private-server" name="server-type" value="p" onChange={this.handleType}/>
                <label htmlFor="private-server">Private</label><br/><br/>

                <button onClick={this.handleSubmit}>Create</button>
            </div>
        );
    }
}
