import React, { Component } from 'react';

export default class Create extends Component {
    constructor(props) {
        super(props);
        this.state = {
            // messages: '',
        };

        this.name = this.props.match.params.name;
        this.handleEnterInput = this.handleEnterInput.bind(this);
    }

    componentDidMount() {
        const chatSocket = new WebSocket(
            'ws://'
            + window.location.host
            + '/ws/'
            + this.name
            + '/'
        );

        chatSocket.onmessage = function(e) {
            const data = JSON.parse(e.data);
            document.querySelector('#chat-log').value += (data.message + '\n');
        };

        chatSocket.onclose = function(e) {
            console.error('Chat socket closed unexpectedly');
        };
    }

    handleEnterInput (e) {
        if (e.keyCode === 13) {  // enter, return
            document.querySelector('#chat-message-submit').click();
        }
    }

    handleSubmit (e) {
        const messageInputDom = document.querySelector('#chat-message-input');
        const message = messageInputDom.value;
        chatSocket.send(JSON.stringify({
            'message': message
        }));
        messageInputDom.value = '';
    }

    render() {
        return (
            <div className="server">
                <textarea id="chat-log" cols="100" rows="20"></textarea><br></br>
                <input id="chat-message-input" type="text" size="100" onKeyUp={this.handleEnterInput}></input>
                <input id="chat-message-submit" type="button" value="Send" onClick={this.handleSubmit}></input>
            </div>
        );
    }
}
