import React, { Component } from 'react';

export default class Join extends Component {
    constructor(props) {
        super(props);
        this.state = {
            message: '',
            server_type: '',
        }

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange (e) {
        this.setState({
            name: e.target.value
        });
    }

    handleType (e) {
        this.setState({
            server_type: e.target.value
        });
    }

    handleSubmit () {
        const requestOptions = {
            method: 'POST',
            headers: {'Content-Type' : 'application/json'},
            body: JSON.stringify({
                name: this.state.name
            }),
        };
        fetch('http://127.0.0.1:8000/api/' + this.state.server_type + '/join/', requestOptions)
            .then((response) => {
                if (response.ok) {
                    this.props.history.push("/" + this.state.server_type + "/" + this.state.name)
                }
                else {
                    this.setState({message: response.statusText})
                }
            })
        }

    render() {
        return(
            <div className="Create">
                {this.state.message}
                <input type="text" onChange={this.handleChange}></input>
                <button onClick={this.handleSubmit}>Join</button>
            </div>
        );
    }
}
